import React from "react"
import axios from "axios"

class Instafeed extends React.Component {
    state = {
        images: [],
    }

    componentDidMount() {
        const fields = "id,caption,media_type,media_url,permalink,thumbnail_url,timestamp,username"
        let user_id = "17841401439222266"
        let token = 'IGQVJYaWFVX3ZASU1BScXB6d0t5RHBSS0ZAfdGFiekV1eGJ2cFp0NW5IYXJYZAmZAWRU5adV9qWnJzTHVJMjJGTEg1aHVQZA2hkWDViN1diSU81MnExTFJKajg2R0RLVHFMTTl2bGJlRS1pOE5ENDhzeHZA5bgZDZD';
        let num_photos = 100;

        axios.get(`https://graph.instagram.com/${user_id}/media?fields=${fields}&access_token=${token}&count=${num_photos}`)
        .then(res => {
            console.log(res.data);
                this.setState({ images: res.data });
        })
        .catch(err => {
            console.log(err)
        })

        console.log(this.state.images);
    }

    render() {
        return(
            <div id="instafeed">
                { this.state.images.map((image) => {
                    return <img src={image/*.images.thumbnail.url*/} />
                })}
            </div>
        )
    }
  }

  export default Instafeed
