import React, { Fragment } from "react"
import { buildUrl } from "react-instafeed"

import useAbortableFetch from '@hooks/useAbortableFetch'
import Image from '@components/Image'

const Instafeed = () => {
    const options = {
        accessToken: 'IGQVJVN0N0NmtNbktkWkVoQlBoRllwUHNlY2x1WEhJOEdsME5ZAZAnpYN2xvRUVmeTlSRGtiVWhIZAEtLSVJYdFBiRDR2ckF3aklUQzRMTzE2M3Y1REt5ODRqT1B5bWZAKSGhJVF9ZAcmdB',
        clientId: '1244631599211499',
        get: 'user', // popular, user
        locationId: null,
        resolution: 'standard_resolution', // thumbnail, low_resolution, standard_resolution
        sortBy: 'most-recent', // none, least-commented, least-liked, least-recent, most-commented, most-liked, most-recent, random
        tagName: null,
        userId: 123,
    }

    const { json, loading, error, abort } = useAbortableFetch(buildUrl(options))
    if (loading) return 'Loading...'
    if (error) return `Error: ${error}`
    if (!json) return null

    const { data, meta, pagination } = json

    return (
      <>
        {// eslint-disable-next-line no-unused-vars
        data &&
          data.map(({ caption, id, images, tags }, index) => {
            const image = images[options.resolution]
            return (
                <Image
                  key={index}
                  url={image.url}
                  title={caption.text}
                  caption={caption.text}
                  width={image.width}
                  height={image.height}
                />
              //</div>
            )
          })}
      </>
    )
  }

  export default Instafeed
