import React from "react"
import axios from "axios"

class Instafeed extends React.Component {
    state = {
        images: [],
        additional_photos: true,
        photo_limit: 0,
    }

    componentDidMount() {
        this.retrievePhotos()
    }

    retrievePhotos() {
        const fields = "id,caption,media_type,media_url,permalink,thumbnail_url,timestamp,username"
        let user_id = "17841401439222266"
        let token = 'IGQVJYaWFVX3ZASU1BScXB6d0t5RHBSS0ZAfdGFiekV1eGJ2cFp0NW5IYXJYZAmZAWRU5adV9qWnJzTHVJMjJGTEg1aHVQZA2hkWDViN1diSU81MnExTFJKajg2R0RLVHFMTTl2bGJlRS1pOE5ENDhzeHZA5bgZDZD'

        this.setState({ photo_limit: this.state.photo_limit + 40})

        axios.get(`https://graph.instagram.com/${user_id}/media?fields=${fields}&access_token=${token}&limit=${this.state.photo_limit}`)
        .then(res => {
            this.setState({ images: res.data.data });
            if (typeof res.data.paging.next === "string") {
                this.setState({ additional_photos: true })
            } else {
                this.setState({ additional_photos: false })  
            }
        })
        .catch(err => {
            console.log(err)
        })
    }

    render() {
        console.log(this.state.additional_photos)

        return(
            <div id="instafeed">
                { this.state.images.map((image) => {
                    return(
                        <div className="col-xs-12 col-sm-6 col-md-4 col-lg-3" key={image.timestamp}>
                            <a href={image.permalink}>
                                <div className="photo-box">
                                    <div className="image-wrap">
                                        <img src={image.media_url} alt={image.caption} />
                                    </div>
                                    <div className="likes"><img src={require('./images/heart.png')} alt="heart" /></div>
                                </div>
                            </a>
                        </div>
                    )
                })}
                {this.state.additional_photos
                    ? <button onClick={this.retrievePhotos}>Load More</button>
                    : null
                }
            </div>
        )
    }
  }

  export default Instafeed
